import React, { useState } from "react";

function FirmCategories() {
  // Track which firms the user has selected
  const [selectedFirms, setSelectedFirms] = useState([]);

  // Here is a data structure with each category and its firms
  const categories = [
    {
      categoryName: "Bulge Bracket Investment Banks (BBs)",
      firms: [
        "JP Morgan",
        "Goldman Sachs",
        "Morgan Stanley",
        "Bank of America",
        "Citi",
        "Barclays",
        "UBS",
        "Deutsche Bank",
      ],
    },
    {
      categoryName: "In-Between-a-Banks (IBABs)",
      firms: [
        "Wells Fargo",
        "RBC",
        "HSBC",
        "BNP Paribas",
        "Mizuho",
        "Nomura",
        "BMO",
        "CITIC",
      ],
    },
    {
      categoryName: "Elite Boutique Investment Banks (EBs)",
      firms: [
        "Centerview",
        "Evercore",
        "Guggenheim",
        "Lazard",
        "Moelis",
        "Perella Weinberg",
        "PJT Partners",
        "Qatalyst",
        "Rothschild",
      ],
    },
    {
      categoryName: "Up-and-Coming Elite Boutique Investment Banks (UCEBs)",
      firms: [
        "LionTree Advisors",
        "Zaoui & Co.",
        "Robey Warshaw",
        "Lakeside Capital Advisers",
        "Dyal Co.",
      ],
    },
    {
      categoryName: "Middle Market Banks (MMs)",
      firms: [
        "Baird",
        "Brown Gibbons Lang & Company",
        "Cowen",
        "Harris Williams",
        "Houlihan Lokey",
        "Janney",
        "Jefferies",
        "JMP",
        "Lincoln International",
        "Macquarie",
        "Needham",
        "Oppenheimer",
        "Piper Sandler",
        "PJ Solomon",
        "Raymond James",
        "Stephens",
        "Stifel",
        "Truist",
        "Wedbush",
        "William Blair",
      ],
    },
    {
      categoryName: "Industry-Specific Boutiques (ISBs)",
      firms: [
        "Leerink",
        "Ziegler",
        "FT Partners",
        "Raine Group",
        "Allen & Co.",
        "Seabury",
        "Telsey Advisory Group",
      ],
    },
    {
      categoryName: "Other Banks (Merchant Banks, Hybrid Firms, and KPOs)",
      firms: [
        "BDT Capital Partners",
        "Tudor Pickering Holt & Co.",
        "Raine Group",
        "Three Ocean Partners",
        "Lepe Partners",
      ],
    },
  ];

  // Toggle a firm in or out of the selectedFirms array
  const handleFirmChange = (firm) => {
    setSelectedFirms((prevSelected) => {
      // If already selected, remove it
      if (prevSelected.includes(firm)) {
        return prevSelected.filter((f) => f !== firm);
      }
      // Otherwise, add it
      return [...prevSelected, firm];
    });
  };

  // Called when the user is done selecting—this is where you’d call your API
  const handleSubmit = () => {
    // For now, just log the selected firms
    console.log("Selected Firms:", selectedFirms);
    // TODO: Replace this console.log with an API call, e.g.:
    // myApi.post('/selected-firms', { firms: selectedFirms })
    //   .then(...)
    //   .catch(...)
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Firm Categories</h2>
      <p style={styles.instructions}>
        Please select the firms you’d like to network with (you can select
        multiple):
      </p>

      {categories.map((cat) => (
        <div key={cat.categoryName} style={styles.categorySection}>
          <h3 style={styles.categoryName}>{cat.categoryName}</h3>
          <ul style={styles.firmList}>
            {cat.firms.map((firm) => (
              <li key={firm} style={styles.firmItem}>
                <label style={styles.label}>
                  <input
                    type="checkbox"
                    checked={selectedFirms.includes(firm)}
                    onChange={() => handleFirmChange(firm)}
                    style={styles.checkbox}
                  />
                  {firm}
                </label>
              </li>
            ))}
          </ul>
        </div>
      ))}

      <button style={styles.button} onClick={handleSubmit}>
        Continue
      </button>
    </div>
  );
}

// Simple inline styles (optional)
const styles = {
  container: {
    maxWidth: "800px",
    margin: "2rem auto",
    padding: "1rem",
    fontFamily: "Arial, sans-serif",
  },
  title: {
    textAlign: "center",
    marginBottom: "0.5rem",
  },
  instructions: {
    textAlign: "center",
    color: "#666",
    marginBottom: "2rem",
  },
  categorySection: {
    marginBottom: "2rem",
  },
  categoryName: {
    fontSize: "1.1rem",
    margin: "0.5rem 0",
  },
  firmList: {
    listStyle: "none",
    paddingLeft: 0,
  },
  firmItem: {
    marginBottom: "0.25rem",
  },
  label: {
    cursor: "pointer",
  },
  checkbox: {
    marginRight: "0.5rem",
  },
  button: {
    display: "block",
    margin: "1rem auto",
    padding: "0.75rem 1.5rem",
    backgroundColor: "#4a67f2",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
};

export default FirmCategories;
