import React, { useState } from "react";
import { categoryCompanies } from './categoriesToCompanies'; // import your mapping file

function FirmCategories({user}) {
    // Which categories the user has selected
    const [selectedCategories, setSelectedCategories] = useState([]);
    const [professionals, setProfessionals] = useState([]);
    // Which categories are “expanded” to show firm names
    const [expandedCategories, setExpandedCategories] = useState({});

    const categories = Object.keys(categoryCompanies);
    const handleToggleCategory = (cat) => {
        setSelectedCategories((prev) =>
        prev.includes(cat) ? prev.filter((c) => c !== cat) : [...prev, cat]
        );
    };

    // Toggle whether a category is selected
    const handleSelectCategory = (categoryName) => {
    setSelectedCategories((prev) => {
      // If it’s already selected, remove it
        if (prev.includes(categoryName)) {
            return prev.filter((cat) => cat !== categoryName);
        }
      // Otherwise, add it
        return [...prev, categoryName];
        });
    };

  // Toggle whether a category is expanded to show firm names
  const handleToggleMoreInfo = (categoryName) => {
    setExpandedCategories((prev) => ({
      ...prev,
      [categoryName]: !prev[categoryName],
    }));
  };


    const handleContinue = async () => {
      try {
        // Build the query string: e.g. ?previous_company=McKinsey&school=Harvard&current_company=Evercore&current_company=Lazard
        const params = new URLSearchParams();

        params.append("previous_company", "Morgan Stanley");
        if (user.university) {
          params.append("school", "University of Michigan");
        }

        // For each selected category, look up its array of companies
        selectedCategories.forEach((category) => {
          const companies = categoryCompanies[category] || [];
          companies.forEach((company) => {
            params.append("current_company", company);
          });
        });

        const response = await fetch(
          `/api/professionals?${params.toString()}`,
          {
            method: "GET",
          }
        );
        if (!response.ok) {
          throw new Error(
            `Network response was not ok: ${response.statusText}`
          );
        }
        const data = await response.json();
        setProfessionals(data); // store the professionals in state
        console.log("Fetched professionals:", data);
      } catch (error) {
        console.error("Error fetching professionals:", error);
        alert("Failed to fetch professionals");
      }
    };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Firm Categories</h1>
      <p style={styles.subtitle}>
        Select any categories you’re interested in. Click “More Info” to see
        example firms in each category.
      </p>

      <div>
        {categories.map((cat) => {
          const isSelected = selectedCategories.includes(cat.categoryName);
          const isExpanded = expandedCategories[cat.categoryName] || false;

          return (
            <div key={cat.categoryName} style={styles.categoryCard}>
              <div style={styles.cardHeader}>
                {/* Big button to select/unselect category */}
                <button
                  style={{
                    ...styles.categoryButton,
                    backgroundColor: isSelected ? "#4a67f2" : "#fff",
                    color: isSelected ? "#fff" : "#333",
                  }}
                  onClick={() => handleSelectCategory(cat.categoryName)}
                >
                  {isSelected ? "Selected" : "Select"}: {cat.categoryName}
                </button>

                {/* Button to toggle the “more info” about the category */}
                <button
                  style={styles.infoButton}
                  onClick={() => handleToggleMoreInfo(cat.categoryName)}
                >
                  {isExpanded ? "Hide Info" : "More Info"}
                </button>
              </div>

              {/* Conditionally render the list of firms if expanded */}
              {isExpanded && (
                <ul style={styles.firmList}>
                  {cat.firms.map((firm) => (
                    <li key={firm} style={styles.firmItem}>
                      {firm}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          );
        })}
      </div>

      <button onClick={handleContinue}>Continue</button>

      <div style={{ marginTop: "2rem" }}>
        <h3>Results:</h3>
        {professionals.map((prof) => (
          <p key={prof.id}>
            {prof.name} — {prof.current_company}
          </p>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "800px",
    margin: "2rem auto",
    padding: "1rem",
    fontFamily: "Arial, sans-serif",
  },
  title: {
    textAlign: "center",
    marginBottom: "0.5rem",
  },
  subtitle: {
    textAlign: "center",
    marginBottom: "1.5rem",
    color: "#555",
  },
  categoryCard: {
    marginBottom: "1.5rem",
    border: "1px solid #ddd",
    borderRadius: "6px",
    padding: "1rem",
    backgroundColor: "#fff",
  },
  cardHeader: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "0.75rem",
  },
  categoryButton: {
    padding: "0.75rem 1rem",
    fontSize: "1rem",
    border: "1px solid #ddd",
    borderRadius: "4px",
    cursor: "pointer",
    fontWeight: 500,
    flex: 1,
    marginRight: "1rem",
    transition: "background-color 0.2s ease",
  },
  infoButton: {
    padding: "0.75rem 1rem",
    fontSize: "0.9rem",
    border: "1px solid #ddd",
    borderRadius: "4px",
    cursor: "pointer",
  },
  firmList: {
    listStyle: "disc",
    marginLeft: "1.5rem",
  },
  firmItem: {
    marginBottom: "0.25rem",
  },
  continueButton: {
    display: "block",
    margin: "2rem auto 0",
    padding: "0.75rem 1.5rem",
    backgroundColor: "#4a67f2",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    fontSize: "1rem",
    fontWeight: 500,
  },
};

export default FirmCategories;
